workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import loan.reports.*;
import java.sql.Timestamp;

public class LoanCollectionReportController extends AbstractReportController
{
    @Binding
    def binding;

    @Script('AppReportSupport')
    def support;

    @FormTitle
    def formTitle = 'Summary of Loan Collection';

    @Service('DailyCollectionReportService')
    def svc;

    def filter = [date: OsirisContext.env.LOCALE_TIME];

    def getReportName() {
        return 'loan/reports/collection/SummaryOfLoanCollections.jasper';
    }

    def getParameters() {
        def env = OsirisContext.env;
        def title = '';
        if( filter.state ) title = ' - ' + filter.state.caption;

        return [
            BRANCH_NAME: env.CLIENT.name,
            USER: OsirisContext.env.USERINFO.firstname+ ' '+ OsirisContext.env.USERINFO.lastname,
            PRINTDATE: new Date(),
            REPORT_TITLE: 'Summary of Loan Collections' + title,
        ];
    }

    def getData() {
        def p = new HashMap(filter);
        if( p.state ) p.state = p.state.code;
        return svc.buildReport( p );
    }
}
