workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import loan.reports.*;
import java.sql.Timestamp;

public class LoanPastDueReportController extends AbstractReportController
{
    @Binding
    def binding;

    @Script('AppReportSupport')
    def support;

    @FormTitle
    def formTitle = 'Summary of Past Due Loans';

    @Service('LoanApplicationReportService')
    def svc;

    def filter = [date: OsirisContext.env.LOCALE_TIME];

    def getReportName() {
        return 'loan/reports/pastdue/SummaryOfPastDueLoans.jasper';
    }

    def getParameters() {
        def env = OsirisContext.env;
        def title = '';
        if( filter.state ) title = ' - ' + filter.state.caption;

        return [
            BRANCH_NAME: env.CLIENT.name,
            USER: "$env.USERINFO.firstname $env.USERINFO.lastname".toString(),
            PRINTDATE: new Date(),
            REPORT_TITLE: 'Summary of Past Due Loans' + title,
        ];
    }

    def getData() {
        def p = new HashMap(filter);
        if( p.state ) p.state = p.state.code;
        return svc.buildReport( p );
    }

}
