workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import loan.reports.*;
import java.sql.Timestamp;

public class LoanReleasesForTheDayReportController extends AbstractReportController
{
    @Binding
    def binding;

    @FormTitle
    def formTitle = 'Daily Releases Report';

    @Service('LoanReleaseReportService')
    def svc;

    def filter = [date: OsirisContext.env.LOCALE_TIME];

    def getReportName() {
        return 'loan/reports/release/Loan_Releases.jasper';
    }

    def getParameters() {
        def env = OsirisContext.env;
        return [
            BRANCH_NAME: OsirisContext.env.CLIENT.name ,
            PRINTDATE: Timestamp.valueOf( filter.date + ' 00:00:00'),
            USER: OsirisContext.env.USERINFO.firstname+ ' '+ OsirisContext.env.USERINFO.lastname
        ];
    }

    def getData() {
        return svc.buildReport( filter );
    }
}
