workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import loan.reports.*;
import java.sql.Timestamp;

public class LoanIncentivesFormController extends AbstractReportController
{
    @Binding
    def binding;

    @FormTitle
    def formTitle = 'Summary of Monthly Incentives Report';

    @Service('LoanReleaseReportService')
    def svc;

    def filter = [date: OsirisContext.env.LOCALE_TIME];

    def getReportName() {
        return 'loan/reports/incentive/ReleasesIncentiveFinal.jasper';
    }

    def getParameters() {
        def env = OsirisContext.env;
        return [
            BRANCH_NAME: env.CLIENT.name,
            USER: "$env.USERINFO.firstname $env.USERINFO.lastname".toString(),
            PRINTDATE: Timestamp.valueOf( filter.date + ' 00:00:00')
        ];
    }

    def getData() {
        return svc.buildReport( filter );
    }
}
