workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;

class ApplicationForReleaseController {

    @Binding
    def binding;

    def entity = [:];
    def account;
    def selectHandler;

    def lookupAccount() {
        return InvokerUtil.lookupOpener("revenueaccounts:lookup", [
            selectHandler: { o ->
                entity.account = o;
                account = o.title;
                binding.refresh("account");
            }
        ]);
    }

    def add() {
        if( selectHandler ) selectHandler( entity );
        return '_close';
    }

}
