                            //return total.join(', ');
                        //}
                        //data.totalCharges = getTotalCharges(accts);

                        return data;
                    },
                    getParameters:{
                        return[
                            BRANCH_NAME: OsirisContext.env.CLIENT.name,
                            PRINTDATE: new Date(),
                            User: OsirisContext.env.USERINFO.firstname + ' ' + OsirisContext.env.USERINFO.lastname
