class LoanLedgerSearchPaymentDateController{
    @Service('LoanLedgerService')
    def svc;

    @Binding
    def binding;

    def selectHandler;
    //def filter=[:];

    def filter = [date: OsirisContext.env.LOCALE_TIME];

    def find(){
        if(!filter.date){
            MsgBox.alert("Date is required");
            return;
        }else
            return "_close";
    }
}
