workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.util.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import com.rameses.common.*;
import java.io.*;
import com.rameses.io.*;

class JBInfoController{
    @Binding
    def binding;

    def appInfo;
    def entity;
    def gtplName;

    void init(){
        appInfo = TemplateProvider.instance.getResult("loan/ui/"+gtplName+".gtpl",[data:entity]);
    }

    def viewJBEmployment(){
        return InvokerUtil.lookupOpener("loan:jbEmployment",[entity:entity,gtplName:"Borrower_JBEmployment"]);
    }

    def viewJBBusiness(){
        return InvokerUtil.lookupOpener("loan:jbBusinessInfo",[entity:entity,gtplName:"Borrower_JBBusiness"]);
    }

    def viewJBOtherIncome(){
        return InvokerUtil.lookupOpener("loan:jbOtherSrcInfo",[entity:entity,gtplName:"Borrower_JBOtherIncome"]);
    }

    def viewJBProfBGround(){
        return InvokerUtil.lookupOpener("loan:jbProfBGround",[entity:entity,gtplName:"Borrower_JBProfBGround"]);
    }

    def viewJBSavings(){
        return InvokerUtil.lookupOpener("loan:jbBankAccts_Savings",[entity:entity,gtplName:"Borrower_JBBank_Savings"]);
    }

    def viewJBChecking(){
        return InvokerUtil.lookupOpener("loan:jbBankAccts_Checking",[entity:entity,gtplName:"Borrower_JBBank_Checking"]);
    }

    def viewJBSiblings(){
        return InvokerUtil.lookupOpener("loan:jbSiblingsInfo",[entity:entity,gtplName:"Borrower_JBOtherRelatedInfo"]);
    }

    def viewJBSpouse(){
        return InvokerUtil.lookupOpener("loan:jbSpouseInfo",[entity:entity,gtplName:"Borrower_JBSpouseInfo"]);
    }

    def close(){
        appInfo=null;
        binding.fireNavigation "_close";
    }
}
