workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.util.*;
import com.rameses.osiris2.client.*;
import com.rameses.common.*;
import java.io.*;
import com.rameses.io.*;

class PBSpouseInfoController{
    @Binding
    def binding;

    def appInfo;
    def entity;
    def gtplName;

    void init(){
        appInfo = TemplateProvider.instance.getResult("loan/ui/"+gtplName+".gtpl",[data:entity]);
    }

    def viewSiblingsOtherIncome(){
        return InvokerUtil.lookupOpener("loan:siblingsOtherSrcInfo",[entity:entity,gtplName:"Borrower_SiblingsOtherIncome"]);
    }

    def viewSiblingsEmployment(){
        return InvokerUtil.lookupOpener("loan:siblingsEmploymentInfo",[entity:entity,gtplName:"Borrower_SiblingsEmployment"]);
    }

    def close(){
        appInfo=null;
        binding.fireNavigation "_close";
   }
}
