workunit.code:

import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;

class CrecomRecomendationController {

    @Binding
    def binding;

    @Service('LoanApplicationService')
    def svc;

    //-- passed by the caller
    def application;
    def selectHandler;

    def crecomrecommendation;

    def post(){
        def param=[objid:application.objid, crecomrecommendation:crecomrecommendation];
        application.crecomrecommendation=svc.saveCrecomRecommendation(param);
        selectHandler(application);
        return "_close";
    }
}
