workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

public class LookupControlAccountController {

    @Service("GLService")
    def service;

    def controlName;

    def selectHandler;
    def selected;

    def listHandler = [
        getColumns: {
            return [
                new Column(name:"item.code", caption:"Acct Code", maxWidth:100),
                new Column(name:"item.name", caption:"Acct Name")
            ]
        },
        fetchList: { o->
            return service.getControlAccounts( [slobject: controlName ] );
        }
    ] as PageListModel;


    public def select() {
        if(!selected.item) throw new Exception("Please select an item");
        selectHandler( selected.item );
        return "_close";
    }

}
