import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.util.*;
import com.rameses.osiris2.client.*;

public class ContactAccountsController {

    @Binding
    def binding;

    def entityHandler;
    def entity;
    def account;
    def acctTypes;

    public void init() {
        entity = entityHandler();
        acctTypes = InvokerUtil.lookupOpeners( "contact:account", [contact: entity] );
    }

}
