workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.util.*;
import com.rameses.osiris2.client.*;

public class ContactTransactionsController {
    @Binding
    def binding;

    def entityHandler;
    def entity;
    def txn;
    def txnTypes;

    public void init() {
        entity = entityHandler();
        txnTypes = InvokerUtil.lookupOpeners( "contact:transaction", [entity: entity] );
        if( txnTypes ) {
            txn = txnTypes[0];
        }
    }
}
